update DOCENTE SET NOMBRE = (SELECT CONCAT(TITULO,' ',(SELECT CONCAT(AP_PATERNO,' ', AP_MATERNO,' ', NOMBRE) FROM PERSONA))) FROM DOCENTE  WHERE DOCENTE.ID = PERSONA.ID;
update DOCENTE SET NOMBRE = DOCENTE.TITULO + PERSONA.AP_PATERNO + PERSONA.AP_MATERNO + PERSONA.NOMBRE WHERE DOCENTE.ID = PERSONA.ID